def verifier_function(response: str) -> bool:
    # Split response into sentences using ., ?, ! followed by whitespace or end of string
    import re
    # Find all sentences (including last one)
    sentences = re.split(r'(?<=[.?!])\s+', response.strip())
    for sentence in sentences:
        stripped = sentence.strip()
        if not stripped:
            continue  # skip empty
        if not stripped.endswith('.'):
            return False
    return True

