def verifier_function(response: str) -> bool:
    import re
    # Split sentences by '.', '?', '!' followed by whitespace or end of string
    # Keep the delimiter for accurate splitting
    sentence_end_re = re.compile(r'([.?!])(\s+|$)')
    pos = 0
    sentences = []
    for match in sentence_end_re.finditer(response):
        end = match.end()
        sentences.append(response[pos:end])
        pos = end
    if pos < len(response):
        sentences.append(response[pos:])
    for sentence in sentences:
        # Strip leading/trailing spaces
        s = sentence.lstrip()
        if not s:
            continue  # skip empty sentences
        # Find first non-space, non-punctuation character
        m = re.match(r'^[\s\W]*([A-Za-z])', s)
        if m:
            first_char = m.group(1)
            if not first_char.isupper():
                return False
    return True

def verifier_function(response: str) -> bool:
    import re
    # Remove leading/trailing spaces
    response = response.strip()
    if not response:
        return False
    # Check that the response starts with a capitalized word
    first_word_match = re.match(r'^\W*([A-Za-z])', response)
    if not first_word_match:
        return False
    if not first_word_match.group(1).isupper():
        return False
    # Split into sentences by . ? ! followed by whitespace or end of string
    sentences = re.split(r'(?<=[.!?])\s+', response)
    for sentence in sentences:
        sentence = sentence.lstrip()
        if not sentence:
            continue
        # Find first non-space, non-punctuation character
        first_char_match = re.match(r'^[\s\W]*([A-Za-z])', sentence)
        if not first_char_match:
            continue
        if not first_char_match.group(1).isupper():
            return False
    return True

def verifier_function(response: str) -> bool:
    import re
    # Split response into words using regex to handle punctuation
